import React from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

import {
    getTotalBasketCount,
    getTotalBasketPrice
} from 'selectors'

const BasketCart = ({ totalBasketCount, totalPrice }) => {
    return (
        <div id='cart' className='cart'>
            <div className='dropdown'>
                <Link
                    to='/basket'
                    // id='dLabel'
                    className='cart-button'
                >
                    {/* <i className='fa fa-fa-shopping-cart'></i> */}
                    <span>
                        {totalBasketCount} item(s) - ${totalPrice}

                        {/* <img className='cart-icon' src='/uploads/icons/shopping-cart.png' alt=''/> */}
                    </span>
                    <i className="fa-solid fa-cart-shopping"></i>
                </Link>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        totalBasketCount: getTotalBasketCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}

export default connect(mapStateToProps, null)(BasketCart)