import React, { Component } from "react";
import { connect } from "react-redux";
import * as R from 'ramda';
import { Link } from 'react-router-dom'
import {
    CarouselProvider,
    Slider,
    Slide,
    Image,
    Dot,
} from 'pure-react-carousel'
import 'pure-react-carousel/dist/react-carousel.es.css';

import {
    fetchPhones,
    loadMorePhones,
    addPhoneToBasket,
    fetchCategories
} from 'actions'
import { getPhones } from 'selectors'
import Layout from 'containers/layout'

class Phones extends Component {
    constructor(props) {
        super(props);
        this.state = { phones: [], priceSortDesc: true };
    }

    componentDidMount() {
        this.props.fetchPhones()
        this.props.fetchCategories()
    }

    componentDidUpdate(prevProps) {
        if (this.props.phones !== prevProps.phones) {
            this.setState({
                phones: this.props.phones
            })
        }
    }

    sort() {
        const sortByPrice = R.sortWith([
            this.state.priceSortDesc ?
                R.descend(R.prop('price')) :
                R.ascend(R.prop('price'))
        ])
        this.setState((state) => ({
            phones: sortByPrice(state.phones),
            priceSortDesc: !state.priceSortDesc
        }))
    }

    addPhone(id) {
        this.props.addPhoneToBasket(id)
        const basket = document.querySelector('#cart')
        basket.classList.add('animated')
        basket.addEventListener('animationend', () => {
            basket.classList.remove('animated')
        })
    }

    renderPhone(phone, index) {
        const shortDescription = `${R.take(60, phone.description)}...`
        return (
            <div className="col-sm-4 col-lg-4 col-md-4 book-list" key={index}>
                <div className="thumbnail">
                    <CarouselProvider
                        naturalSlideWidth={290}
                        naturalSlideHeight={290}
                        totalSlides={phone.images.length}
                    >
                        <Slider>
                            {phone.images.map((image, index) => (
                                <Slide index={index} key={index}>
                                    <Image src={image} />
                                </Slide>
                            ))}
                        </Slider>
                        <div className='preview-list'>
                            <div>
                                {phone.images.map((image, index) => (
                                    <Dot slide={index} className='preview-link' key={index}>
                                        <Image className='preview-img' src={image} />
                                    </Dot>
                                ))}
                            </div>
                        </div>
                    </CarouselProvider>
                    <div className="caption">
                        <h4 className="pull-right">${phone.price}</h4>
                        <h4>
                            <Link to={`/phones/${phone.id}`}>
                                {phone.name}
                            </Link>
                        </h4>
                        <p>{shortDescription}</p>
                        <p className="itemButton">
                            <button
                                className="btn btn-primary btn-primary-border-left"
                                onClick={() => this.addPhone(phone.id)}
                            >
                                Buy now!
                            </button>
                            <Link
                                to={`/phones/${phone.id}`}
                                className='btn btn-default btn-primary-border-right'
                            >
                                More info
                            </Link>
                        </p>
                    </div>
                </div>
            </div>
        )
    }

    render() {
        const { loadMorePhones } = this.props
        return (
            <Layout>
                <button
                    className="btn-sort"
                    onClick={() => this.sort()}
                >
                    Sort by price
                    <img src='/uploads/icons/sort.png' alt='' />
                </button>
                <div className="books row">
                    {this.state.phones.map((phone, index) => this.renderPhone(phone, index))}
                </div>
                <div className="row">
                    <div className="col-md-12">
                        <button
                            onClick={loadMorePhones}
                            className='btn btn-primary load-more'
                        >
                            Load More
                        </button>
                    </div>
                </div>
            </Layout>
        )
    }
}

const mapStateToProps = (state, ownProps) => ({
    phones: getPhones(state, ownProps)
})

const mapDispatchToProps = {
    fetchPhones,
    loadMorePhones,
    addPhoneToBasket,
    fetchCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(Phones);